/* IMPORT */
import File from '../utils/file.js';
import ProviderAbstractJSON from './abstract_json.js';
/* MAIN */
class ProviderJSON extends ProviderAbstractJSON {
    /* PUBLIC API */
    fileRead(filePath, encoding) {
        return File.read(filePath, encoding);
    }
    fileReadSync(filePath, encoding) {
        return File.readSync(filePath, encoding);
    }
    fileWrite(filePath, data, options) {
        return File.write(filePath, data, options);
    }
    fileWriteSync(filePath, data, options) {
        return File.writeSync(filePath, data, options);
    }
    fileWatch(filePath, callback) {
        return File.watch(filePath, callback);
    }
}
/* EXPORT */
export default ProviderJSON;
